	  private float GMua;
	  private float GBan;
      
	public  Info(float vn30, float GMua, float GBan) {
		this.vn30=vn30;
		this.GMua=GMua;
		this.GBan=GBan;
		
	
	}

	public Info() {
		// TODO Auto-generated constructor stub
	}

	public float getVn30() {
		return vn30;
	}

